import dayjs from 'dayjs';
import TextField from '@mui/material/TextField';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { StaticDatePicker } from '@mui/x-date-pickers/StaticDatePicker';

import {useEffect, useState} from 'react';
import { Card, Typography,Button } from "@mui/material";
function Time(){

function TimeSelect(){
  const [StartTime, setStartTime] = useState(dayjs(Date()).subtract(3,'year'));
  const [EndTime, setEndTime] = useState(dayjs(Date()));
  const [anchorE1, setanchorE1] = useState(null);
  const handleClick = (event)=>{
    setanchorE1(event.currentTarget);
  }
  const handleClose=()=>{
    setanchorE1(null);
  }
  const callender=()=>{
  const SelectStartTime=()=>{

    return (
     <LocalizationProvider dateAdapter={AdapterDayjs}>
     <Typography>开始时间</Typography>
        <StaticDatePicker
        displayStaticWrapperAs="desktop"
        value={StartTime}
        onChange={(newValue) => {
          setStartTime(newValue);
        }}
        renderInput={(params) => <TextField {...params} />}
        onClose={handleClose}
        maxDate={EndTime}
      />
   </LocalizationProvider>
  );
  };
  const SelectEndTime =()=> {

    return (
     <LocalizationProvider dateAdapter={AdapterDayjs}>
      <Typography>结束时间</Typography>
        <StaticDatePicker
        displayStaticWrapperAs="desktop"
        value={EndTime}
        onChange={(newValue) => {
          setEndTime(newValue);
        }}
        renderInput={(param) => <TextField {...param} />}
        onClose={handleClose}
        minDate={StartTime}
        maxDate={Date()}
      />
   </LocalizationProvider>
  );
  }
  }
  return (
      <Typography align="center">
        <Typography>{anchorE1===null?<Button onClick={handleClick} id={"starttime"}>{StartTime.get('year')}-{StartTime.get('month')}-{StartTime.get('dates')}</Button>:<card><callender.SelectStartTime/></card>}</Typography>
        <Typography>-</Typography>
        <Typography>{anchorE1===null?<Button onClick={handleClick} id={"endtime"}>{EndTime.get('year')}-{EndTime.get('month')}-{EndTime.get('dates')}</Button>:<SelectEndTime/>}</Typography>
      </Typography>
  );
}
  return (
    <Card style={{height:"100%",width:"100%"}}>

      <TimeSelect/>
    </Card>
  );
}
export default Time;
