import dayjs from 'dayjs';
import TextField from '@mui/material/TextField';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { StaticDatePicker } from '@mui/x-date-pickers/StaticDatePicker';

import {useEffect, useState} from 'react';
import { Card, Typography,Button } from "@mui/material";
function Time(){

const [StartTime, setStartTime] = useState(dayjs(Date()).subtract(3,'year'));
const [EndTime, setEndTime] = useState(dayjs(Date()));
function TimeSelect(){
  const Start=0;
  const End=1;
  let anchorE1 = new Array(false,false);
  function handleClick (id){
    anchorE1[id]=true;
    alert(anchorE1[id]);
  }
  const handleClose=(id)=>{
    anchorE1[id]=false;
  }
  const SelectStartTime=()=>{

    return (
     <LocalizationProvider dateAdapter={AdapterDayjs}>
     <Typography>开始时间</Typography>
        <StaticDatePicker
        displayStaticWrapperAs="desktop"
        value={StartTime}
        onChange={(newValue) => {
          setStartTime(newValue);
        }}
        renderInput={(params) => <TextField {...params} />}
        onClose={handleClose}
        maxDate={EndTime}
      />
   </LocalizationProvider>
  );
  };
  const SelectEndTime =()=> {

    return (
     <LocalizationProvider dateAdapter={AdapterDayjs}>
      <Typography>结束时间</Typography>
        <StaticDatePicker
        displayStaticWrapperAs="desktop"
        value={EndTime}
        onChange={(newValue) => {
          setEndTime(newValue);
        }}
        renderInput={(param) => <TextField {...param} />}
        onClose={handleClose}
        minDate={StartTime}
        maxDate={Date()}
      />
   </LocalizationProvider>
  );
  }
  return (
      <Typography align="center">
        <Typography>{anchorE1[Start]===false?<Button onClick={handleClick(Start)} id={"starttime"}>{StartTime.get('year')}-{StartTime.get('month')}-{StartTime.get('dates')}</Button>:<SelectStartTime/>}</Typography>
        <Typography>-</Typography>
        <Typography>{anchorE1[End]===false?<Button onClick={handleClick(End)} id={"endtime"}>{EndTime.get('year')}-{EndTime.get('month')}-{EndTime.get('dates')}</Button>:<SelectEndTime/>}</Typography>
      </Typography>
  );
}
  return (
    <Card style={{height:"100%",width:"100%"}}>

      <TimeSelect/>
    </Card>
  );
}
export default Time;
